    $(function() {
      $('#language-picker')
        .on('click', function() {
          var source = _getScreenName($(this));

          if (window.gas) {
            window.gas.sendUIEvent({
              action: 'clicked',
              actionSubject: 'selectMenu',
              actionSubjectId: 'languageSelectMenu',
              source: source        
            });
          }
        })
        .change(function() {
          var $this = $(this)
          var source = _getScreenName($this);
          var selectedValue = $this.val();

