            window.gas.sendUIEvent({
              action: 'selected',
              actionSubject: 'selectMenuOption',
              actionSubjectId: 'languageSelectMenuOption',
              source: source,
              attributes: {
                'language': selectedValue,
              }
            });
          }
        });
    });
